var mongoose = require('mongoose')
  , mongooseTypes = require('mongoose-types')
  , _schema = require('./schema')
  , everyauth = require('everyauth');
mongooseTypes.loadTypes(mongoose);
var Email = mongoose.SchemaTypes.Email;

module.exports = function weibo (schema, opts) {
  schema.add(_schema);

  schema.static('createWithWeibo', function (weibo, accessToken, callback) {
    var params = {
      weibo: {
          id: weibo.id
        , username: weibo.screen_name
        , profilePicture: weibo.profile_image_url
        , counts: {
              follows: weibo.followers_count
            , friends: weibo.friends_count
          }
        , cache: weibo
      }
    };

    // TODO Only do this if password module is enabled
    //      Currently, this is not a valid way to check for enabled
    if (everyauth.password)
      params[everyauth.password.loginKey()] = "weibo:" + weibo.id; // Hack because of way mongodb treate unique indexes

    this.create(params, callback);
  });
};
